"use client";
import { useState } from "react";
// import { Card, CardContent } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { motion, AnimatePresence } from "framer-motion";

export default function IPLBetCalculator() {
  const [teamAName, setTeamAName] = useState("SRH");
  const [teamBName, setTeamBName] = useState("PBKS");
  const [oddsA, setOddsA] = useState(1.75);
  const [oddsB, setOddsB] = useState(3.0);
  const [betA, setBetA] = useState(500);
  const [betB, setBetB] = useState(null);
  const [result, setResult] = useState(null);

  const calculate = () => {
    const calculatedBetB = (betA * oddsA) / oddsB;
    const totalStake = betA + calculatedBetB;
    const profitA = betA * oddsA - totalStake;
    const profitB = calculatedBetB * oddsB - totalStake;
    setBetB(calculatedBetB.toFixed(2));
    setResult({
      totalStake: totalStake.toFixed(2),
      profitA: profitA.toFixed(2),
      profitB: profitB.toFixed(2),
    });
  };

  return (
    <div className="p-6 md:p-10 max-w-2xl mx-auto text-center">
      <motion.h1
        className="text-3xl md:text-4xl font-bold text-indigo-600 mb-6"
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        IPL Live Betting Profit Calculator
      </motion.h1>

      <motion.div
        className="bg-white rounded-2xl shadow-lg p-6 space-y-6"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.3 }}
      >
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <Input
            placeholder="Team A Name"
            value={teamAName}
            onChange={(e) => setTeamAName(e.target.value)}
          />
          <Input
            placeholder="Team B Name"
            value={teamBName}
            onChange={(e) => setTeamBName(e.target.value)}
          />
          <Input
            type="number"
            step="0.01"
            value={oddsA}
            onChange={(e) => setOddsA(parseFloat(e.target.value))}
            placeholder="Team A Odds"
          />
          <Input
            type="number"
            step="0.01"
            value={oddsB}
            onChange={(e) => setOddsB(parseFloat(e.target.value))}
            placeholder="Team B Odds"
          />
        </div>
        <Input
          type="number"
          value={betA}
          onChange={(e) => setBetA(parseFloat(e.target.value))}
          placeholder="Bet on Team A (₹)"
        />
        <Button onClick={calculate} className="w-full">
          Calculate
        </Button>
      </motion.div>

      <AnimatePresence>
        {result && (
          <motion.div
            className="mt-8 bg-gradient-to-r from-green-100 to-blue-100 rounded-xl p-6 shadow-md"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 0.6 }}
          >
            <p className="text-lg font-semibold text-gray-700">
              Recommended Bet on {teamBName}: ₹{betB}
            </p>
            <p className="text-md text-gray-600">Total Stake: ₹{result.totalStake}</p>
            <p className="text-md text-green-700">Profit if {teamAName} Wins: ₹{result.profitA}</p>
            <p className="text-md text-blue-700">Profit if {teamBName} Wins: ₹{result.profitB}</p>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}
